mapper.select:
select
id, payment_id, purchase_id, purchase_type, purchase_time, payable_money, paid_money, unpaid_money, this_money
from frameproject.capital_payment_bill


        and id = #{id}


        and payment_id = #{paymentId}


        and purchase_id = #{purchaseId}


        and purchase_type = #{purchaseType}


        and purchase_time = #{purchaseTime}


        and payable_money = #{payableMoney}


        and paid_money = #{paidMoney}


        and unpaid_money = #{unpaidMoney}


        and this_money = #{thisMoney}
