mapper.select:
select
depot_number, depot_name, depot_address, charge_id, contact_number, remarks, state
from frameproject.base_depot


        and depot_number = #{depotNumber}


        and depot_name = #{depotName}


        and depot_address = #{depotAddress}


        and charge_id = #{chargeId}


        and contact_number = #{contactNumber}


        and remarks = #{remarks}


        and state = #{state}
