mapper.select:
select
delivery_id, delivery_time, customer, salesmen, disrate, dismoney, receivables, remarks, approver, founder,
found_time, update_time, order_id, return_id, receipt_id, cav_id, billing_id, last_approval_time,
approval_state, delivery_state, order_state, approval_remarks
from frameproject.sale_delivery


        and delivery_id = #{deliveryId}


        and delivery_time = #{deliveryTime}


        and customer = #{customer}


        and salesmen = #{salesmen}


        and disrate = #{disrate}


        and dismoney = #{dismoney}


        and receivables = #{receivables}


        and remarks = #{remarks}


        and approver = #{approver}


        and founder = #{founder}


        and found_time = #{foundTime}


        and update_time = #{updateTime}


        and order_id = #{orderId}


        and return_id = #{returnId}


        and receipt_id = #{receiptId}


        and cav_id = #{cavId}


        and billing_id = #{billingId}


        and last_approval_time = #{lastApprovalTime}


        and approval_state = #{approvalState}


        and delivery_state = #{deliveryState}


        and order_state = #{orderState}


        and approval_remarks = #{approvalRemarks}
