mapper.select:
select
id, order_id, product_id, product_num, depot, sale_unit_price, sale_money
from frameproject.sale_order_details


        and id = #{id}


        and order_id = #{orderId}


        and product_id = #{productId}


        and product_num = #{productNum}


        and depot = #{depot}


        and sale_unit_price = #{saleUnitPrice}


        and sale_money = #{saleMoney}
