mapper.select:
select
  id, inventory_id, product_name, product_id,  product_spe, product_type, product_unit, system_num, inventory_num, unit_pl, remarks
from (select * from StockInventoryDetails where work_point_id = #{workPointId})

select
id, inventory_id, product_name, product_id,  product_spe, product_type, product_unit, system_num,
inventory_num, unit_pl, remarks
from frameproject.stock_inventory_details


        and id = #{id}


        and inventory_id = #{inventoryId}


        and product_name = #{productName}


        and product_id = #{productId}


        and product_spe = #{productSpe}


        and product_type = #{productType}


        and product_unit = #{productUnit}


        and system_num = #{systemNum}


        and inventory_num = #{inventoryNum}


        and unit_pl = #{unitPl}


        and remarks = #{remarks}
